    import ColorPicker from '../../src/components/color-picker/index.example';

    import DataTable from '../../src/components/data-table/index.example';

    import DatePicker from '../../src/components/date-picker/index.example';

    import Dropdown from '../../src/components/dropdown/index.example';

    import Editor from '../../src/components/editor/index.example';

    import FileSelector from '../../src/components/file-selector/index.example';

    import Form from '../../src/components/form/index.example';

    import GlobalNavigation from '../../src/components/global-navigation/index.example';

    import Images from '../../src/components/images/index.example';

    import Layout from '../../src/components/layout/index.example';

    import Loadings from '../../src/components/loadings/index.example';

    import Lookup from '../../src/components/lookup/index.example';

    import MediaObject from '../../src/components/media-object/index.example';

    import Modal from '../../src/components/modal/index.example';

    import Navigation from '../../src/components/navigation/index.example';

    import Notification from '../../src/components/notification/index.example';

    import PageHeader from '../../src/components/page-header/index.example';

    import Pagination from '../../src/components/pagination/index.example';

    import Phone from '../../src/components/phone/index.example';

    import Popover from '../../src/components/popover/index.example';

    import Process from '../../src/components/process/index.example';

    import SlidePannel from '../../src/components/slide-pannel/index.example';

    import Tab from '../../src/components/tab/index.example';

    import Tiles from '../../src/components/tiles/index.example';

    import TimePicker from '../../src/components/time-picker/index.example';

const components = {
    
       'ActivityTimeline' : ActivityTimeline,
    
       'Badge' : Badge,
    
       'Breadcrumb' : Breadcrumb,
    
       'ButtonGroup' : ButtonGroup,
    
       'ButtonIcon' : ButtonIcon,
    
       'Buttons' : Buttons,
    
       'Cards' : Cards,
    
       'Cascader' : Cascader,
    
       'ColorPicker' : ColorPicker,
    
       'DataTable' : DataTable,
    
       'DatePicker' : DatePicker,
    
       'Dropdown' : Dropdown,
    
       'Editor' : Editor,
    
       'FileSelector' : FileSelector,
    
       'Form' : Form,
    
       'GlobalNavigation' : GlobalNavigation,
    
       'Images' : Images,
    
       'Layout' : Layout,
    
       'Loadings' : Loadings,
    
       'Lookup' : Lookup,
    
       'MediaObject' : MediaObject,
    
       'Modal' : Modal,
    
       'Navigation' : Navigation,
    
       'Notification' : Notification,
    
       'PageHeader' : PageHeader,
    
       'Pagination' : Pagination,
    
       'Phone' : Phone,
    
       'Popover' : Popover,
    
       'Process' : Process,
    
       'SlidePannel' : SlidePannel,
    
       'Tab' : Tab,
    
       'Tiles' : Tiles,
    
       'TimePicker' : TimePicker,
    
}
export default class main extends Component {

    buildExample(ReactElement,index) {
        let components = [];
        if (ReactElement instanceof Array) {
            ReactElement.forEach((v, index) => {
                let element = (
                        <DemoBlock key={index}>
                            <DemoExample id={v.id}>
                                <DemoItem>{v.id}</DemoItem>
                                <div>{v.intro}</div>
                                 {v.element}
                            </DemoExample>

                            <DemoCode>
                                <pre className='prettyprint'>
                                    {v.code ? v.code : ReactElementToString(v.element)}
                                </pre>
                                <textarea  style={{width:'1px',height:'1px',opacity:'0'}}
                                 defaultValue= {v.code ? v.code : ReactElementToString(v.element)} />
                            </DemoCode>
                        </DemoBlock>
                );
                components.push(element);
            });
        } else {
            components = React.createElement(ReactElement);
        }
        return (
            <Demo id={index} key={index}>
                <DemoHeader>{index}</DemoHeader>
                {components}
            </Demo>
        );
    }

    render() {
        let renderComponents = _.map(components,(v,i) => {
               return this.buildExample(v,i);
        });
        return (
            <div id="mainContent">
               {renderComponents}
            </div>
        );
    }
}